import React, { useState, useEffect, useCallback } from 'react';

import MoviesList from './components/MoviesList';
import AddMovie from './components/AddMovie';
import './App.css';

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try{
      const response = await fetch('https://react-e93b4-default-rtdb.firebaseio.com/movies.json');
      if(!response.ok){
        throw new Error('Something went wrong');
      }
      const data = await response.json();

      const loadedMovies = [];

      for(const key in data) {
        loadedMovies.push({
          id: key,
          title: data[key].title,
          openingText: data[key].openingText,
          releaseDate: data[key].releaseDate,
        })
      }

      setMovies(loadedMovies);
    }catch(error){
      setError(error.message);
    }
    setIsLoading(false);
  },[]);

  const addMovieHandler= useCallback(async (movie) =>{
    const options = {
      method: 'POST',
      body: JSON.stringify(movie),
      headers: {
        'Content-Type': 'application/json'
      }
    };
    const response = await fetch('https://react-e93b4-default-rtdb.firebaseio.com/movies.json',options);
    const data = await response.json();
    console.log(data);
  },[]);

  useEffect(() => {
    fetchMoviesHandler();
    addMovieHandler();
  },[addMovieHandler, fetchMoviesHandler]);

  let content = <p>Found no movies.</p>

  if(movies.length >0){
    content = <MoviesList movies={movies} />;
  }

  if(error){
    content = <p>{error}</p>;
  }

  if(isLoading){
    content = <p>Loading</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {content}
      </section>
    </React.Fragment>
  );
}

export default App;
